import { useState } from 'react'
import { Course } from './components/Course'

const Total = ({ parts }) => {
  let sum = 0
  for (let [key, value] of Object.entries(parts)) {
    sum += value.exercises
  }
  console.log(sum)
  return (
    <div>
      <b>total of exercises {sum}</b>
    </div>
  )
}



const App = () => {
  const courses = [
    {
      name: 'Half Stack application development',
      id: 1,
      parts: [
        {
          name: 'Fundamentals of React',
          exercises: 10,
          id: 1
        },
        {
          name: 'Using props to pass data',
          exercises: 7,
          id: 2
        },
        {
          name: 'State of a component',
          exercises: 14,
          id: 3
        },
        {
          name: 'Redux',
          exercises: 11,
          id: 4
        }
      ]
    }, 
    {
      name: 'Node.js',
      id: 2,
      parts: [
        {
          name: 'Routing',
          exercises: 3,
          id: 1
        },
        {
          name: 'Middlewares',
          exercises: 7,
          id: 2
        }
      ]
    }
  ]
  const course = courses[0]
  return (
  <div>
  <ul style={{ listStyleType: "none", paddingLeft: 0 }}>
    {courses.map(course =>
      <li key={course.id}>
        <Course course={course}/>
        <Total parts={course.parts}/>
      </li>
    )}
  </ul>
  </div>
  )
}

export default App
