import { useState } from 'react'


const Header = (props) => {
  return (
    <div>
      <h1>{props.courseName}</h1>
    </div>
  )
}

const Part = (props) => {
  return (
    <div>
      <p>
        {props.name} {props.numExercises}
      </p>
    </div>
  )
}

const Content = (props) => {
  const parts = props.parts
  return (
    <div>
      <ul style={{ listStyleType: "none", paddingLeft: 0 }}>
        {parts.map(part => 
          <li key={part.id}>
            <Part name={part.name} numExercises={part.exercises}/>
          </li>
        )}
      </ul>
    </div>
  )
}

const Total = ({ parts }) => {
  let sum = 0
  for (let [key, value] of Object.entries(parts)) {
    sum += value.exercises
  }
  console.log(sum)
  return (
    <div>
      <b>Total of exercises {sum}</b>
    </div>
  )
}

const Course = ({ course }) => {
  console.log(course.name)
  return (
    <div>
    <Header courseName={course.name}/>
    <Content parts={course.parts}/>
    </div>
  )
}

const App = () => {
  const courses = [
    {
      name: 'Half Stack application development',
      id: 1,
      parts: [
        {
          name: 'Fundamentals of React',
          exercises: 10,
          id: 1
        },
        {
          name: 'Using props to pass data',
          exercises: 7,
          id: 2
        },
        {
          name: 'State of a component',
          exercises: 14,
          id: 3
        },
        {
          name: 'Redux',
          exercises: 11,
          id: 4
        }
      ]
    }, 
    {
      name: 'Node.js',
      id: 2,
      parts: [
        {
          name: 'Routing',
          exercises: 3,
          id: 1
        },
        {
          name: 'Middlewares',
          exercises: 7,
          id: 2
        }
      ]
    }
  ]
  const course = courses[0]
  return (
  <div>
  <Course course={course} />
  <Total parts={course.parts}/>
  <ul>

  </ul>
  </div>
  )
}

export default App
